INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Sweet corn', 'maize', 'Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 61&deg;F and 95&deg;F. (Show &deg;C/cm)', '8 - 12 inches apart', 'Harvest in 11-14 weeks.', 'All beans, cucumber, melons, peas, pumpkin, squash, amaranth', 'Celery.', 'Piick and cook within an hour. Remove the silks and outer leaves. Best flavour if microwave about 4 minutes per cob. Can be barbequed wrapped in foil. Cook large amounts in a stock pot until test soft. Sprinkle with black pepper and dip in butter.', 'NA', 'http://gardenate.com/image/40/thumb/150/150; http://gardenate.com/image/34/thumb/150/150; http://gardenate.com/image/85/thumb/150/150', 'http://gardenate.com/plant/Sweet%2Bcorn', 3, 1);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Cowpeas', 'Black eye peas, Southern peas', 'Easy to grow. When soil begins to warm up. After frosts finish.. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 59&deg;F and 95&deg;F. (Show &deg;C/cm)', 'Rows 1 Metre apart', 'Harvest in 11-14 weeks.', 'NA', 'NA', 'Young leaves can be cooked and used like spinach and are very high in protein. The young pods are edible.', 'NA', 'NA', 'http://gardenate.com/plant/Cowpeas', 4, 2);
