INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Chives', 'Garden chives', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 86&deg;F. (Show &deg;C/cm)', '2 inches apart', 'Harvest in 7-11 weeks.', 'Carrots, Tomatoes, Parsley, Apples', 'NA', 'Use raw in salads or as a mild onion flavour in cooked dishes.', 'NA', 'http://gardenate.com/image/69/thumb/150/150', 'http://gardenate.com/plant/Chives', 6, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Coriander', 'Cilantro, Chinese parsley', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 77&deg;F. (Show &deg;C/cm)', 'Thin to 18 inches', 'Harvest in 30-45 days.', 'Dill, Chervil, Anise, Cabbages, Carrots', 'Fennel', 'Use the leaves to flavour hot meals or add fresh to salads.', 'The seeds can be dried and ground up for curries.', 'http://gardenate.com/image/52/thumb/150/150', 'http://gardenate.com/plant/Coriander', 7, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Collards', 'Collard greens, Borekale', 'Easy to grow. Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 46&deg;F and 86&deg;F. (Show &deg;C/cm)', '16 - 20 inches apart', 'Harvest in 8-11 weeks.', 'Dwarf (bush) beans, beets, celery, cucumber, onions, marigold, nasturtium, rhubarb, aromatic herbs (sage, dill, chamomile)', 'Climbing (pole) beans, tomato, peppers (chili, capsicum), eggplant (aubergine), strawberry, mustard', 'Slice and steam or use in stir-fry', 'NA', 'http://gardenate.com/image/147/thumb/150/150', 'http://gardenate.com/plant/Collards', 8, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Choko/Chayote', 'Chayote squash, christophene, chouchou, mirliton', 'Easy to grow. Plant whole mature fruit when one produces a shoot at one end.. Best planted at soil temperatures between 59&deg;F and 86&deg;F. (Show &deg;C/cm)', '39 inches apart', 'Harvest in approximately 17 weeks. Best when fruit is light green and not more than 6cm long.', 'Cucumbers', 'NA', 'Chokos can be peeled and chopped to use in stews, soup or as a stir fry vegetable. Cooked or raw, it has a very mild flavour and is commonly served with seasonings e.g., salt, butter and pepper or in a dish with other vegetables and/or flavourings. It can also be boiled, stuffed, mashed or pickled', 'NA', 'http://gardenate.com/image/99/thumb/150/150', 'http://gardenate.com/plant/Choko~Chayote', 9, 1);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Burdock', 'Gobo (Japanese Burdock)', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 68&deg;F. (Show &deg;C/cm)', '24 inches apart', 'Harvest in 17-18 weeks.', 'Best grown in separate bed.', 'NA', 'Harvest in the first year when the burdock root is very crisp and has a sweet, mild, and pungent flavour with a little muddy harshness that can be reduced by soaking julienne/shredded roots in water for five to ten minutes. Immature flower stalks may also be harvested in late spring, before flowers appear; the taste resembles that of artichoke, to which the burdock is related. It is a key ingredient in the traditional Dandelion and Burdock beer.', 'NA', 'NA', 'http://gardenate.com/plant/Burdock', 10, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Chinese cabbage', 'Wong bok, wong nga pak', 'Easy to grow. Sow direct in the garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 68&deg;F. (Show &deg;C/cm)', '12 inches apart', 'Harvest in 8-10 weeks. Harvest whole head or you can take a few leaves at a time.', 'Dwarf (bush) beans, beets, celery, cucumber, onions, marigold, nasturtium, rhubarb, aromatic herbs (sage, dill, chamomile, coriander), lettuce, potatoes', 'Climbing (pole) beans, tomato, peppers (chilli, capsicum), eggplant (aubergine), strawberry, mustard', 'Use in stir-fry . Has a milder flavour than regular cabbage.Shred the inner leaves and stems to use in coleslaw salad.', 'NA', 'NA', 'http://gardenate.com/plant/Chinese%2Bcabbage', 11, 1);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Chilli peppers', 'Hot peppers', 'Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 64&deg;F and 95&deg;F. (Show &deg;C/cm)', '16 - 20 inches apart', 'Harvest in 9-11 weeks. Wear gloves to pick &lsquo;hot&lsquo; chilies.', 'Best grown in a separate bed as chillis need plenty of light and air circulation.', 'NA', 'Wash, dry, and free whole. Use them direct from the freezer (no need to defrost). Wear plastic gloves or wash your hands thoroughly after handling and cutting to avoid accidentally rubbing chilli juice onto your mouth or eyes!', 'Will freeze well.', 'http://gardenate.com/image/129/thumb/150/150', 'http://gardenate.com/plant/Chilli%2Bpeppers', 12, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Cabbage', 'NA', 'Easy to grow. Grow in seed trays, and plant out in 4 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 41&deg;F and 64&deg;F. (Show &deg;C/cm)', '20 - 30 inches apart', 'Harvest in 11-15 weeks.', 'Dwarf (bush) beans, beets, celery, cucumber, onions, marigold, nasturtium, rhubarb, aromatic herbs (sage, dill, chamomile, thyme)', 'Climbing (pole) beans, tomato, peppers (chili, capsicum), eggplant (aubergine), strawberry, mustard, parsnip', 'Young spring cabbage can be chopped and added to salad greens. Steaming preserves the goodness and flavour of cabbage. Can also be used in stir-fry. Red cabbage chopped and cooked with brown sugar, red wine, onions, vinegar and stock is served with boiled bacon or pork.', 'NA', 'http://gardenate.com/image/71/thumb/150/150', 'http://gardenate.com/plant/Cabbage', 13, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Celery', 'NA', 'Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 54&deg;F and 70&deg;F. (Show &deg;C/cm)', '6 - 12 inches apart', 'Harvest in 17-18 weeks.', 'Not applicable as celery needs to be close together to encourage blanching.', 'Sweetcorn', 'Chop and use raw in salad or braised in hot dishes.', 'NA', 'http://gardenate.com/image/128/thumb/150/150', 'http://gardenate.com/plant/Celery', 14, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Chicory', 'Witloof, Belgian endive', 'Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 68&deg;F. (Show &deg;C/cm)', '10 - 12 inches apart', 'Harvest in 16-24 weeks. Will need forcing before final harvest.', 'Carrots, onions, Florence fennel, tomatoes.', 'NA', 'Good in salads. Grill lightly with butter. Bake with ham and cheese.', 'NA', 'http://gardenate.com/image/105/thumb/150/150', 'http://gardenate.com/plant/Chicory', 15, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Cauliflower', 'NA', 'Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 86&deg;F. (Show &deg;C/cm)', '24 - 39 inches apart', 'Harvest in 15-22 weeks.', 'Dwarf (bush) beans, beets, celery, cucumber, onions, marigold, nasturtium, rhubarb, aromatic herbs (sage, dill, chamomile)', 'Climbing (pole) beans, tomato, peppers (chili, capsicum), eggplant (aubergine), strawberry, mustard', 'Cauliflower can be steamed. Young ones can be broken into small pieces and added raw to salad. Cook briefly and add to curry mix. Traditionally served with cheese sauce. Add tomato slices for colour.', 'NA', 'http://gardenate.com/image/126/thumb/150/150', 'http://gardenate.com/plant/Cauliflower', 16, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Celeriac', 'NA', 'Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 46&deg;F and 70&deg;F. (Show &deg;C/cm)', '18 - 31 inches apart', 'Harvest in 14-28 weeks.', 'Beans, brassicas, carrots, leeks, lettuce, peas, sage, tomatoes, onions', 'NA', 'Cook whole, scrubbed and peeled. Or slice or dice.Tastes like celery.', 'NA', 'http://gardenate.com/image/127/thumb/150/150', 'http://gardenate.com/plant/Celeriac', 17, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Cape Gooseberry', 'Golden Berry, Inca Berry', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 77&deg;F. (Show &deg;C/cm)', '20 inches apart', 'Harvest in 14-16 weeks.', 'Will happily grow in a flower border', 'NA', 'The berry is the size of a cherry tomato, is very aromatic and full of tiny seeds. They are delicious eaten fresh or can be made into jam. They can be added to salads, desserts and cooked dishes, they are delicious stewed with other fruit, especially apples. They also go well in savoury dishes with meat or seafood.', 'Can be preserved dried.', 'http://gardenate.com/image/56/thumb/150/150; http://gardenate.com/image/54/thumb/150/150; http://gardenate.com/image/59/thumb/150/150', 'http://gardenate.com/plant/Cape%2BGooseberry', 18, 1);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Cardoon', 'NA', 'Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 55&deg;F and 77&deg;F. (Show &deg;C/cm)', '20 - 59 inches apart', 'Harvest in 34-35 weeks.', 'Best grown in separate bed.', 'NA', 'Cut off the base and leaves, then cut the stalks into pieces. Boil the stalks for around 20 minutes until tender: drain, and peel off the surface of the stalks. Add precooked cardoons to a variety of dishes, they go well with mushrooms.', 'NA', 'http://gardenate.com/image/120/thumb/150/150', 'http://gardenate.com/plant/Cardoon', 19, 1);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Carrot', 'NA', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 46&deg;F and 86&deg;F. (Show &deg;C/cm)', '2 - 12 inches apart', 'Harvest in 12-18 weeks.', 'Onions, Leeks, Lettuce, Sage, Peas, Radishes, Tomatoes, Beans, Celery, Rosemary', 'Parsnips, Beetroot, Dill, Brassicas, Fennel', 'Steamed or raw carrots are tasty. Cook them in a small amount of water until nearly dry then add a pat of butter and teasp of brown sugar to glaze. They can be added to most casserole-type dishes. Grate raw carrots and add to salads', 'NA', 'http://gardenate.com/image/65/thumb/150/150; http://gardenate.com/image/160/thumb/150/150', 'http://gardenate.com/plant/Carrot', 20, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Capsicum', 'Bell peppers, Sweet peppers', 'Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 64&deg;F and 95&deg;F. (Show &deg;C/cm)', '8 - 20 inches apart', 'Harvest in 10-12 weeks. Cut fruit off with sharp knife.', 'Egg plant (Aubergine), Nasturtiums, Basil, Parsley, Amaranth', 'NA', 'Can be sliced and seeded and used raw in salads. Or brush with olive oil, roast at a high temperature until the skin changes colour then put in a covered dish until cool and rub off the skin and remove seeds.', 'Will freeze successfully without blanching if seeded and sliced.', 'http://gardenate.com/image/47/thumb/150/150; http://gardenate.com/image/46/thumb/150/150', 'http://gardenate.com/plant/Capsicum', 21, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Angelica', 'NA', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 77&deg;F. (Show &deg;C/cm)', '18 inches apart', 'Harvest in approximately 18 months. Angelica archangelica has slightly dull leaves, not shiny..', 'Any herbs that like damp, shady areas - mint, lemon balm', 'NA', 'The stems can be candied and used to decorate cakes and pastries. Pick the stems in the second year.', 'NA', 'http://gardenate.com/image/124/thumb/150/150', 'http://gardenate.com/plant/Angelica', 22, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Artichokes (Globe)', 'NA', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 59&deg;F and 64&deg;F. (Show &deg;C/cm)', '63 - 79 inches apart', 'Harvest in 42-57 weeks.', 'Needs a lot of space. Best in separate bed', 'NA', 'Pick buds before scales develop brown tips. If you have lots of small buds, they can be fried in olive oil and eaten whole. Rinse in plenty of cold water to remove earwigs or other insects.', 'NA', 'http://gardenate.com/image/109/thumb/150/150; http://gardenate.com/image/14/thumb/150/150', 'http://gardenate.com/plant/Artichokes%2B(Globe)', 23, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Cucumber', 'NA', 'Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 61&deg;F and 95&deg;F. (Show &deg;C/cm)', '16 - 24 inches apart', 'Harvest in 8-10 weeks. Cut fruit off with scissors or sharp knife.', 'Nasturtiums, Beans, Celery, Lettuce, Sweet Corn, Cabbages, Sunflowers, Coriander, Fennel, Dill, Sunflowers', 'Potato, Tomatoes', 'Pick frequently before the fruit become too big. Use raw in salads, peeled if preferred.', 'NA', 'http://gardenate.com/image/13/thumb/150/150; http://gardenate.com/image/83/thumb/150/150; http://gardenate.com/image/84/thumb/150/150', 'http://gardenate.com/plant/Cucumber', 24, 1);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Asparagus', 'NA', 'Easy to grow. Plant as crowns. Best planted at soil temperatures between 61&deg;F and 86&deg;F. (Show &deg;C/cm)', '8 - 16 inches apart', 'Harvest in 2-3 years. Plant &lsquo;crowns&lsquo; to harvest earlier .', 'Parsley, Basil, Nasturtiums, Lettuce', 'Garlic, Onions, and root vegetables', 'Steaming is traditional, then coating with melted butter or hollandaise sauce. Alternatively break in short lengths, and cook quickly in hot oil in a wok and sprinkle with soy sauce or balsamic vinegar. NOTE: The asparagus berries are poisonous. Only the young shoots are edible.', 'NA', 'http://gardenate.com/image/66/thumb/150/150', 'http://gardenate.com/plant/Asparagus', 25, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Asparagus Pea', 'Winged bean', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 59&deg;F and 68&deg;F. (Show &deg;C/cm)', '8 - 10 inches apart', 'Harvest in 8-11 weeks. Pick early, pick often.', 'Best grown in separate bed', 'NA', 'Cook quickly by steaming and serve with just a touch of butter and they are said to taste like their namesake .', 'NA', 'http://gardenate.com/image/111/thumb/150/150', 'http://gardenate.com/plant/Asparagus%2BPea', 26, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Basil', 'NA', 'Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 64&deg;F and 95&deg;F. (Show &deg;C/cm)', '8 - 10 inches apart', 'Harvest in 10-12 weeks. Pick before flowering.', 'Tomato', 'NA', 'Basil is commonly used fresh in cooked recipes. It is generally added at the last moment, as cooking quickly destroys the flavour. Tear rather than chop. The fresh herb can be kept for a short time in plastic bags in the refrigerator, or for a longer period in the freezer, after being blanched quickly in boiling water.', 'NA', 'http://gardenate.com/image/22/thumb/150/150; http://gardenate.com/image/23/thumb/150/150', 'http://gardenate.com/plant/Basil', 27, 1);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Beans - broad beans, fava beans', 'Fava bean', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 43&deg;F and 75&deg;F. (Show &deg;C/cm)', '6 - 10 inches apart', 'Harvest in 12-22 weeks. Pick frequently to encourage more pods.', 'Dill, Potatoes', 'NA', '"The fresh beans are eaten steamed or boiled. As the beans mature it is better to remove their tough outer skins after cooking. The leafy top shoots of the adult plants can be picked and steamed after flowering. Small beans can be eaten whole in the pods.', 'Will freeze well. Remove from pods and blanch.', 'http://gardenate.com/image/158/thumb/150/150; http://gardenate.com/image/93/thumb/150/150; http://gardenate.com/image/159/thumb/150/150; http://gardenate.com/image/92/thumb/150/150', 'http://gardenate.com/plant/Beans%2B-%2Bbroad%2Bbeans%2C%2Bfava%2Bbeans', 28, 1);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Beans - climbing', 'Pole beans, Runner beans, Scarlet Runners', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 61&deg;F and 86&deg;F. (Show &deg;C/cm)', '4 - 8 inches apart', 'Harvest in 9-11 weeks.', 'Sweetcorn, spinach, lettuce, summer savory, dill, carrots, brassicas, beets, radish, strawberry, cucumbers, zucchini, tagates minuta (wild marigold)', 'Alliums (Chives, leek, garlic, onions), Florence fennel', 'Use young in salads - blanch and cool.', 'Will freeze well.', 'http://gardenate.com/image/42/thumb/150/150; http://gardenate.com/image/38/thumb/150/150', 'http://gardenate.com/plant/Beans%2B-%2Bclimbing', 29, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Beetroot', 'Beets', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 45&deg;F and 77&deg;F. (Show &deg;C/cm)', '8 - 12 inches apart', 'Harvest in 7-10 weeks.', 'Onions, Silverbeet (Swiss Chard), Lettuce, Cabbage, Dwarf Beans, Dill, Peas. Strawberries', 'Asparagus, Carrots, Sweetcorn, Spinach', 'Apart from boiling whole for salads, beetroot roast well, cut in wedges. They also make a tasty salad grated raw with carrot and a little fresh orange juice.', 'NA', 'http://gardenate.com/image/67/thumb/150/150; http://gardenate.com/image/113/thumb/150/150', 'http://gardenate.com/plant/Beetroot', 30, 1);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Borage', 'Burrage, Bugloss', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 77&deg;F. (Show &deg;C/cm)', '8 inches apart', 'Harvest in 8-10 weeks. Use leaves before flowers appear, otherwise they will be &lsquo;hairy&lsquo;. .', 'Strawberry, tomatoes, zucchini/squash. Deters pests from many plants.', 'NA', 'Has a slight cucumber taste which goes well in salads and when cooked with silver beet or cabbage. The flowers make a pretty drink decoration when frozen in an iceblock.', 'NA', 'http://gardenate.com/image/53/thumb/150/150', 'http://gardenate.com/plant/Borage', 31, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Broccoli', 'NA', 'Easy to grow. Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 45&deg;F and 86&deg;F. (Show &deg;C/cm)', '14 - 20 inches apart', 'Harvest in 10-16 weeks. Cut flowerhead off with a knife..', 'Dwarf (bush) beans, beets, celery, cucumber, onions, marigold, nasturtium, rhubarb, aromatic herbs (sage, dill, chamomile, oregano)', 'Climbing (pole) beans, tomato, peppers (chilli, capsicum), eggplant (aubergine), strawberry, mustard', 'The stem (peeled), leaves, and flowerhead are all edible. Steam for best flavour. Peel large stalks, slice and steam. Goes well with blue cheese sauce.', 'NA', 'http://gardenate.com/image/20/thumb/150/150; http://gardenate.com/image/19/thumb/150/150; http://gardenate.com/image/21/thumb/150/150', 'http://gardenate.com/plant/Broccoli', 32, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Brussels sprouts', 'NA', 'Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 45&deg;F and 86&deg;F. (Show &deg;C/cm)', '18 - 24 inches apart', 'Harvest in 14-28 weeks. Pick sprouts when small. .', 'Dwarf (bush) beans, beets, celery, cucumber, onions, marigold, nasturtium, rhubarb, aromatic herbs (sage, dill, chamomile)', 'Climbing (pole) beans, tomato, peppers (chili, capsicum), eggplant (aubergine), strawberry, mustard', 'Remove any discoloured outer leaves. Cut in half and steam with other vegetables. Do not overcook as that produces the distinctive smell that puts people off eating Brussels sprouts! They go well with a chopped tomato and onion mix.Traditionally served with roasted chestnuts for Xmas dinner in UK.', 'NA', 'http://gardenate.com/image/125/thumb/150/150', 'http://gardenate.com/plant/Brussels%2Bsprouts', 33, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Pumpkin', 'NA', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 68&deg;F and 90&deg;F. (Show &deg;C/cm)', '35 - 47 inches apart', 'Harvest in 15-20 weeks.', 'Sweet Corn', 'Potatoes', 'Cut up, remove the skin and roast with other vegetables or meat. Young crisp shoots with young leaves can be cooked and eaten - stewed in coconut milk they are popular in Melanesia. Remove any strings and tough parts and stew until tender, or cook as a vegetable in boiling water 3-5 minutes.', 'NA', 'http://gardenate.com/image/140/thumb/150/150', 'http://gardenate.com/plant/Pumpkin', 34, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Potato', 'NA', 'Plant tuber. Best planted at soil temperatures between 50&deg;F and 86&deg;F. (Show &deg;C/cm)', '12 - 16 inches apart', 'Harvest in 15-20 weeks. Dig carefully, avoid damaging the potatoes.', 'Peas, Beans, Brassicas, Sweetcorn, Broad Beans, Nasturtiums, Marigolds', 'Cucumber, Pumpkin, Sunflowers, Tomatoes, Rosemary', 'Peeled or unpeeled and scrubbed, potatoes can be boiled, baked, fried and roasted. - The only way they are not used is raw. Keep in a pot of cold water after peeling, otherwise they will discolour.', 'NA', 'http://gardenate.com/image/151/thumb/150/150; http://gardenate.com/image/16/thumb/150/150', 'http://gardenate.com/plant/Potato', 35, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Peas', 'NA', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 8&deg;C and 24&deg;C. (Show &deg;F/in)', '5 - 8 cm apart', 'Harvest in 9-11 weeks. Pick the pods every day to increase production.', 'Potatoes', 'NA', 'Raw straight from the pod in the garden is best! Raw in salads. Steamed lightly. Small pods can be steamed whole.', 'NA', 'http://gardenate.com/image/94/thumb/150/150', 'http://gardenate.com/plant/Peas', 36, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Parsnip', 'NA', 'Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 43&deg;F and 70&deg;F. (Show &deg;C/cm)', '3 - 4 inches apart', 'Harvest in 17-20 weeks. Best flavour if harvested after a frost..', 'Swiss Chard (Silverbeet), Capsicum, Peas, Potatoes, Beans, Radishes, Garlic', 'Carrot, Celery, Brassicas', 'Peel and roast with vegetables or meat. The sweetish flavour of parsnips enhances most other vegetables.', 'NA', 'http://gardenate.com/image/60/thumb/150/150; http://gardenate.com/image/61/thumb/150/150', 'http://gardenate.com/plant/Parsnip', 37, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Parsley', 'curly leaf parsley or flat leaf (Italian) parsley', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 86&deg;F. (Show &deg;C/cm)', '8 - 12 inches apart', 'Harvest in 9-19 weeks. Cut stalks from outer part of plant.', 'Carrots, Chives, Tomatoes, Asparagus', 'Potatoes', 'Use the leaves and stems to add flavour and colour. Can be cooked in dishes such as ratatouille, Traditionally used in white sauce.', 'NA', 'http://gardenate.com/image/36/thumb/150/150; http://gardenate.com/image/58/thumb/150/150', 'http://gardenate.com/plant/Parsley', 38, 1);
