INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('NZ Spinach', 'Warrigal greens', 'Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 64&deg;F and 95&deg;F. (Show &deg;C/cm)', '18 - 24 inches apart', 'Harvest in 8-10 weeks. Pick the tips.', 'Does better if alone.', 'NA', 'Can be used as a substitute for real spinach in cooking. Pick the growing tips at about 8-10cm (4-6 in). To remove oxalates it&lsquo;s a good idea to blanch the leaves for 3 minutes or so, then rinse the leaves in cold water before using them in salads or for cooking.(ABC TV)', 'NA', 'http://gardenate.com/image/137/thumb/150/150', 'http://gardenate.com/plant/NZ%2BSpinach', 43, 2);
