INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Mint', 'Garden mint', 'Easy to grow. Grow in trays and plant out or start from cuttings. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 70&deg;F and 75&deg;F. (Show &deg;C/cm)', '12 inches apart', 'Harvest in 8-12 weeks. Cut leaves from top with scissors.', 'Cabbages, Tomatoes', 'NA', 'Mint adds a fresh flavour if chopped and sprinkled over salads. And is traditionally used mixed with vinegar and sugar to make mint sauce for lamb.', 'NA', 'http://gardenate.com/image/135/thumb/150/150', 'http://gardenate.com/plant/Mint', 46, 2);
