INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Luffa', 'Loofah, plant sponge', 'Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 68&deg;F and 86&deg;F. (Show &deg;C/cm)', '18 - 30 inches apart', 'Harvest in 11-12 weeks. Use as a back scratcher.', 'Peas, Beans, Onions, Sweetcorn', 'Potatoes', 'The luffa flowers and fruits are soft and edible when young and are sometimes cooked and eaten like squash or okra. Loofah has been an important food source in many Asian cultures. The leaves and vines should not be eaten.', 'NA', 'http://gardenate.com/image/133/thumb/150/150', 'http://gardenate.com/plant/Luffa', 48, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Lettuce', 'NA', 'Easy to grow. Sow in garden, or start in seed trays and plant out in 4-6 weeks.. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 8&deg;C and 27&deg;C. (Show &deg;F/in)', '20 - 30 cm apart', 'Harvest in 8-12 weeks.', 'Carrots, Onions, Strawberries, Beets, Brassicas, Radish, Marigold, Borage, Chervil, Florence fennel, leeks.', 'Parsley, Celery', 'Wash well, spin or shake dry and use in salads and sandwiches', 'NA', 'http://gardenate.com/image/31/thumb/150/150; http://gardenate.com/image/32/thumb/150/150', 'http://gardenate.com/plant/Lettuce', 49, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Lemon Balm', 'Sweet balm,', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 77&deg;F. (Show &deg;C/cm)', '10 inches apart', 'Harvest in 8-10 weeks. Cut back tall stems to prevent flowering.', 'Good to attract bees', 'NA', 'As a herb tea or added to fruit punch. Can be used to replace lemon, used sparingly, in desserts and with stewed fruit. Chop leaves into salad. Better used fresh than dried.', 'NA', 'http://gardenate.com/image/49/thumb/150/150', 'http://gardenate.com/plant/Lemon%2BBalm', 50, 1);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Kale', 'Borecole', 'Easy to grow. Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 46&deg;F and 86&deg;F. (Show &deg;C/cm)', '20 - 39 inches apart', 'Harvest in 7-9 weeks.', 'Dwarf (bush) beans, beets, celery, cucumber, onions, marigold, nasturtium, rhubarb, aromatic herbs (sage, dill, camomile)', 'Climbing (pole) beans, tomato, peppers (chilli, capsicum), eggplant (aubergine), strawberry, mustard', 'Strong flavoured and nutritious vegetable. Wash well and chop finely then steam. A tomato or cheese sauce will mask the flavour if too strong.', 'NA', 'http://gardenate.com/image/155/thumb/150/150; http://gardenate.com/image/156/thumb/150/150; http://gardenate.com/image/90/thumb/150/150', 'http://gardenate.com/plant/Kale', 51, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Kohlrabi', 'NA', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 46&deg;F and 86&deg;F. (Show &deg;C/cm)', '4 - 10 inches apart', 'Harvest in 7-10 weeks.', 'Beets, celery, cucumber, onions, marigold, nasturtium, rhubarb, aromatic herbs (sage, dill, chamomile)', 'Climbing (pole) beans, tomato, peppers (chilli, capsicum), eggplant (aubergine), strawberry, mustard', 'Use when young. Scrub well, cut off leaf stalks, roots and woody parts. Young ones do not need peeling. Can be grated raw for salads or cut in pieces and steam. Use in casseroles.', 'NA', 'http://gardenate.com/image/132/thumb/150/150', 'http://gardenate.com/plant/Kohlrabi', 52, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Leeks', 'NA', 'Easy to grow. Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 46&deg;F and 86&deg;F. (Show &deg;C/cm)', '4 - 8 inches apart', 'Harvest in 15-18 weeks. Loosen with a fork rather than pull by hand..', 'Carrots', 'NA', 'Trim off the roots and any damaged leaves. Young ones can be used whole with some of the green leaves. Wash thoroughly as the earth tends to get inside. Chop and fry in butter (or olive oil) until tender. Can be added to casserole meals, allowing time to cook through. Leek and mushroom make a tasty combination for a tart filling.', 'NA', 'http://gardenate.com/image/41/thumb/150/150; http://gardenate.com/image/75/thumb/150/150', 'http://gardenate.com/plant/Leeks', 53, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Jerusalem Artichokes', 'Sunchoke', '"Easy to grow. Plant tubers about 5cm (1.5"") deep.. Best planted at soil temperatures between 46&deg;F and 59&deg;F. (Show &deg;C/cm)"', '12 - 18 inches apart', 'Harvest in 15-20 weeks.', 'Tomatoes, cucumbers', 'NA', 'Scrape clean or peel (add a tsp of lemon or vinegar to the water to stop the tubers browning). Steam, boil, or use in artichoke soup (make with artichokes and some stock). Caution - because they contain &lsquo;resistent starch&lsquo; Jerusalem Artichokes are a great promoter of flatulence in some individuals.', 'NA', 'http://gardenate.com/image/97/thumb/150/150', 'http://gardenate.com/plant/Jerusalem%2BArtichokes', 54, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Daikon', 'Japanese radish, Lo Bok', 'Easy to grow. Direct in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 68&deg;F. (Show &deg;C/cm)', '6 inches apart', 'Harvest in 8-10 weeks. Dig daikon carefully. They are rather brittle..', 'Chervil, cress,lettuce, leeks, spinach, strawberries, tomatoes', 'Gherkins, hyssop', 'Daikon radish can be eaten simmered, stir fried, grated, pickled or baked. Its leaves are also edible and can be used in recipes that call for turnip greens, and its seeds make sprouts to eat in salads or in sandwiches.', 'NA', 'http://gardenate.com/image/122/thumb/150/150', 'http://gardenate.com/plant/Daikon', 55, 2);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Dill', 'NA', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 68&deg;F. (Show &deg;C/cm)', '6 inches apart', 'Harvest in 8-12 weeks. Use leaves before flowering.', 'Cabbage, Coriander, Fennel, tomatoes, broccoli', 'NA', 'Dill leaves can be used fresh or dried in salads, meats, vegetable dishes and soups. Freshly cut leaves enhance the flavour of dips, herb butter, soups, salads, fish dishes, and salads. Both the flowering heads and seeds are used in flavoured vinegars and oils. Used whole or ground, the seeds add zest to bread, cheese, and salad dressing.', 'NA', 'http://gardenate.com/image/131/thumb/150/150', 'http://gardenate.com/plant/Dill', 56, 1);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Eggplant', 'Aubergine', 'Grow in seed trays, and plant out in 4-6 weeks. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 75&deg;F and 90&deg;F. (Show &deg;C/cm)', '24 - 30 inches apart', 'Harvest in 12-15 weeks. Cut fruit with scissors or sharp knife.', 'Beans, capsicum, lettuce, amaranth, thyme', 'Potatoes', 'Cut and use the same day if possible. Slice, no need to peel, and fry in olive oil.  Brush with oil and grill or bake. Or microwave,plain, for about 4 minutes on high. Makes a good substitute for pasta in lasagne or moussaka. Can be smoked over a gas ring or barbecue, cooled and peeled and used to make dips.', 'NA', 'http://gardenate.com/image/24/thumb/150/150; http://gardenate.com/image/116/thumb/150/150', 'http://gardenate.com/plant/Eggplant', 57, 1);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Endive', 'NA', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 59&deg;F and 77&deg;F. (Show &deg;C/cm)', '8 - 12 inches apart', 'Harvest in 10-11 weeks.', 'beans, brassicas, carrots, cucumbers, chervil, sage.', 'Alliums (garlic and onions)', 'Very tasty topped with grated swiss cheese and grilled for a couple of minutes to crisp up the cheese and wilt the leaves. Can use in salads additional to lettuce, but needs a flavoursome dressing if you aren&lsquo;t overly fond of bitterness.', 'NA', 'http://gardenate.com/image/148/thumb/150/150', 'http://gardenate.com/plant/Endive', 58, 2);
