INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('French tarragon', 'NA', 'Plant cuttings or root division. Best planted at soil temperatures between 50&deg;F and 77&deg;F. (Show &deg;C/cm)', '16 - 24 inches apart', 'Harvest in 30-40 days. Pick leaves when young for best flavour.', 'Aubergine (Eggplant) and Capsicum (Peppers)', 'NA', 'Tarragon goes well with fish, pork, beef, poultry, game, potatoes, tomatoes, carrots, and most vegetables.� Tarragon can be used in cream sauces, herbed butters and vinegars, soups, sour creams, and yogurt. However, it can be overpowering in large amounts.', 'NA', 'http://gardenate.com/image/39/thumb/150/150', 'http://gardenate.com/plant/French%2Btarragon', 60, 1);
