INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Strawberry Plants', 'NA', 'Easy to grow. Plant with crown (of roots) just covered.. Best planted at soil temperatures between 50&deg;F and 68&deg;F. (Show &deg;C/cm)', '12 - 39 inches apart', 'Harvest in approximately 11 weeks. Strawberries bruise easily when ripe, handle carefully. Pick with a small piece of stem attached..', 'Better in a bed on their own to allow good sun and air circulation', 'If you are using rotation beds, avoid putting strawberries where you have grown tomatoes, potatoes, peppers or eggplant', 'Pick strawberries and eat them straight from the garden - warm from the sunshine, delicious! Strawberries can be used in any dessert needing soft fruit or berries. Summer pudding which also has raspberries and blackberries or boysenberries, mousse, trifle, dipped in melted chocolate or just with cream. Sprinkle a bowl of strawberries with balsamic vinegar and a little sugar to enhance the flavour and colour.', 'NA', 'http://gardenate.com/image/150/thumb/150/150; http://gardenate.com/image/145/thumb/150/150', 'http://gardenate.com/plant/Strawberry%2BPlants', 76, 1);
