INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Salsify', 'Vegetable oyster', 'Easy to grow. Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 86&deg;F. (Show &deg;C/cm)', '6 - 8 inches apart', 'Harvest in 14-21 weeks.', 'Beans, Brassicas, Carrots, Celeriac, Endive, Kohl-rabi, Leeks, Lettuce, Alliums, Spinach', 'NA', 'Wash and scrape the roots then boil before frying or roasting. They can also be used to make a creamed soup.', 'NA', 'http://gardenate.com/image/142/thumb/150/150', 'http://gardenate.com/plant/Salsify', 93, 3);
INSERT INTO plants (name, alternateName, sowInstructions, spaceInstructions, harvestInstructions, compatiblePlants, avoidInstructions, culinaryHints, culinaryPreservation, image, url, id, wateringInterval) VALUES ('Sage', 'Common Sage', 'Sow in garden. Sow seed at a depth approximately three times the diameter of the seed. Best planted at soil temperatures between 50&deg;F and 77&deg;F. (Show &deg;C/cm)', '20 inches apart', 'Harvest in approximately 18 months. Time reduced if grown from cuttings.', 'Broccoli, Cauliflower, Rosemary, Cabbage and Carrots', 'NA', 'The leaves are used to flavour stuffing and meat dishes. Sage keeps well if dried.', 'NA', 'http://gardenate.com/image/3/thumb/150/150; http://gardenate.com/image/25/thumb/150/150', 'http://gardenate.com/plant/Sage', 94, 3);
